const format = msg.format
if (format == "html") {
    let htmlmessage = "<body>" + msg.formatted_payload + "</body>"
    msg.formatted_payload = htmlmessage
}
return msg;

const wakeWord = /^!meow/i
const catBot = /^CatBot\s*$/i
const help = /help/i
const user = msg.userId
const room = msg.topic
let message = msg.payload
if (!user.includes("catbot")) {
    if (wakeWord.test(message) && !help.test(message) && message.length > 6) {
        let command = message.replace(wakeWord, '').split(' ')
        command.shift()
        node.status({ fill: "green", shape: "dot", text: "CatBot Awake! " + command });
        msg.command = command
        msg.format = null
        return [msg,null];
    } else if (wakeWord.test(message) && help.test(message) || wakeWord.test(message) && message.length < 7 || catBot.test(message)) {
        let command = message.replace(wakeWord, '').split(' ')
        command.shift()
        command.splice(0, 1, "help")
        node.status({ fill: "blue", shape: "dot", text: "CatBot kitty helping!" });
        msg.command = command
        msg.format = "html"
        msg.payload = "CatBot Kitty Helping"
        msg.formatted_payload = "<p> <b> #### CatBot Kitty Helps You! #### </b><br><br> <b>Usage: </b> !meow &lt;command&gt;</p>"
        let helpArr = Object.values(global.get("help","file"))
        let helpList
        for (let i = 0; i < helpArr.length; i++) {
            if (i != 0) {
                helpList += helpArr[i]
            } else {
                helpList = helpArr[i]
            }
        }
        helpList = helpList + "<li><b><em> help </em></b> - ask for CatBot's Kitty Help </li>"
        msg.formatted_payload = msg.formatted_payload + "<ul>" + helpList + "</ul>"
        return [null,msg];
    } else if (room == global.get("matrix.rooms.catbot","file")) {
        let command = message.replace(wakeWord, '').split(' ')
        if (help.test(message)) {
            let command = message.replace(wakeWord, '').split(' ')
            command.splice(0, 1, "help")
            node.status({ fill: "blue", shape: "dot", text: "CatBot kitty helping!" });
            msg.command = command
            msg.format = "html"
            msg.payload = "CatBot Kitty Helping"
            msg.formatted_payload = "<p> <b> #### CatBot Kitty Helps You! #### </b><br><br> <b>Usage: </b> !meow &lt;command&gt;</p>"
            let helpArr = Object.values(global.get("help", "file"))
            let helpList
            for (let i = 0; i < helpArr.length; i++) {
                helpList += helpArr[i]
            }
            helpList = helpList + "<li><b><em> help </em></b> - ask for CatBot's Kitty Help </li>"
            msg.formatted_payload = msg.formatted_payload + "<ul>" + helpList + "</ul>"
            return [null, msg];
        }
        node.status({ fill: "green", shape: "dot", text: "CatBot Awake! " + command });
        msg.command = command
        msg.format = null
        return [msg, null];
    } else {
        node.status({ fill: "red", shape: "dot", text: "CatBot Asleep! zzzzz" });
        node.done();
    }
} else {
    node.status({ fill: "red", shape: "dot", text: "CatBot Asleep! zzzzz" });
    node.done();
}
